insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('GEST_CANCELLAZIONI','PWD_SORTEGGI','Se attiva la verticalizzazione, contiene la password per poter cancellare un sorteggio.');
