insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('IMPORT-EXPORT-SIVBG','URL_WS_REPERTORIO','Tale parametro deve contenere la URL del web service che la regione Umbria espone per effettuare le operazioni di import/export dei procedimenti (es:http://10.101.223.226:8090/RepertorioProc/services/RepertorioWS)');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('IMPORT_CCIAA','CONNECTIONSTRING','E'' la stringa di connessione al database normalizzato importato dalla camera di commercio.');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('IMPORT_CCIAA','PROVIDER','E'' il provider per la connessione al database della camera di commercio ( utile per istanziare la PersonalLib2). I valori che può assumere a seconda della stringa di connessione, sono: OleDb, OracleClient, PervasiveClient o SqlClient. ');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('INFOCAMERA','IDESPORTAZIONE','E'' l''id dell''esportazione configurata per la Camera del Commercio');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('INFOCAMERA','PASSWORD','E'' la password da utilizzare per autenticarsi al sistema di invio automatico');
