insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PAGAMENTI_MIP_RPCSUAP','INDIRIZZOPROXY','Indica l''indirizzo del proxy (eventuale) usato dai componenti di comunicazione col sistema di pagamento');
