insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PAGAMENTI_MIP_RPCSUAP','URLSERVERPAGAMENTO','indica l''URL del Server di Pagamento');
