insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PEOPLE','UID_NODO_NLA','Identificatore univoco del nodo NLA di People nell''istallazione (se presente un solo nodo NLA-People va lasciato a 1)');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PEOPLE','URL_INSERIMENTO_ALLEGATIPEOPLE','URL per inviare a People eventuali allegati prodotti dall''elaborazione dell''istanza dentro VBG tramite notifica attivita'' (Es. http://peopledemo:8080/AllegatiService/services/allegatiService)');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PEOPLE','URL_NOTIFICA_PEOPLE','URL per notificare a People l''arrivo della pratica in VBG (va cambiato solo l''IP e la porta. Es. http://suaper.lepida.it:8080/BEService/services/setEventService). Parametro obbligatorio');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PEOPLE','URL_PUBBLICO_VISURA','URL per notificare a People l''URL per fare visura delle pratiche (solitamente va cambiato solo l''IP e la porta. Es. http://localhost:8080/NLAPeople/services/NLAVisuraService). Parametro obbligatorio');
insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PEOPLE','VERTICALIZZAZIONE_RAVENNA','I valori ammessi sono S o N. Se posto a S, all''interno del codice vengono fatte operazioni richieste soltanto per il Comune e la Provincia di Ravenna, per sapere nel dettaglio le personalizzazioni fatte fare riferimento al documento Sigepro2_NLAPeople.doc della commessa 1359-2008 sul software della Qualita'' (Valore di default = N).');
