insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PEOPLE','XMLMOD_TIPOCATASTO_F','Parametro per individuare il TIPO CATASTO FABBRICATI della localizzazione nella pratica people tramite identificativo XML_MOD');
