insert  into `verticalizzazioniparametribase`(`MODULO`,`PARAMETRO`,`DESCRIZIONE`) values ('PROTOCOLLO_SIPRWEB','URLTIPIDOCUMENTO','E'' l''URL per invocare la lista dei tipi documento');
